import React, { Component } from "react";

import api from "../api";
import { useParams } from "react-router-dom";

function withParams(Component) {
  return (props) => <Component {...props} params={useParams()} />;
}

class NewQuestion extends Component {
  constructor(props) {
    super(props);

    this.handleQuestionChange = this.handleQuestionChange.bind(this);
  }

  state = {
    question: "",
    answers: ["", "", "", "", "", "", "", "", "", ""],
    correctAnswer: 1,
  };

  handleQuestionChange(e) {
    this.setState({
      question: e.target.value,
    });
  }

  handleAnswerChange(e, i) {
    let newAnswers = [...this.state.answers];
    newAnswers[i] = e.target.value;

    this.setState({
      answers: newAnswers,
    });
  }

  render() {
    return (
      <div className="row m-4">
        <div className="col-sm-12 col-md-8 col-lg-6 mx-auto">
          <div className="form-group row">
            <label for="question" className="col-sm-2 col-form-label">
              Pergunta
            </label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                id="questionInput"
                name="question"
                onChange={this.handleQuestionChange}
                value={this.state.question}
              />
            </div>
          </div>
          <div className="mt-4">
            {this.state.answers.map((answer, i) => {
              return (
                <div className="form-group row mt-3" key={i}>
                  <label for={"answer" + i} className="col-sm-2 col-form-label">
                    Resposta {i + 1}
                  </label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      className="form-control"
                      name={"answer" + i}
                      onChange={(e) => this.handleAnswerChange(e, i)}
                      value={answer}
                    />
                  </div>
                </div>
              );
            })}
          </div>
          <div className="form-group row mt-4">
            <label for="correctAnswer" className="col-sm-2 col-form-label">
              Resposta correta
            </label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                id="questionInput"
                name="correctAnswer"
                onChange={this.handleQuestionChange}
                value={this.state.question}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withParams(NewQuestion);
