import React, { useState } from "react";
import { Link } from "react-router-dom";

import { useAuth } from "../context/AuthContext";

function Signup() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const { signup } = useAuth();

  const handleClick = () => {
    signup(name, email, password, confirmPassword);
  };

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };

  return (
    <div className="row m-4">
      <div className="col-sm-12 col-md-8 col-lg-6 mx-auto">
        <div className="form-group row">
          <label for="name" className="col-sm-4 col-form-label">
            Nome
          </label>
          <div className="col-sm-8">
            <input
              type="text"
              className="form-control"
              id="nameInput"
              name="name"
              onChange={handleNameChange}
              value={name}
            />
          </div>
        </div>
        <div className="form-group row mt-3">
          <label for="email" className="col-sm-4 col-form-label">
            Email
          </label>
          <div className="col-sm-8">
            <input
              type="email"
              className="form-control"
              id="emailInput"
              name="email"
              onChange={handleEmailChange}
              value={email}
            />
          </div>
        </div>
        <div className="form-group row mt-3">
          <label for="password" className="col-sm-2 col-form-label">
            Password
          </label>
          <div className="col-sm-10">
            <input
              type="password"
              className="form-control"
              id="passwordInput"
              name="password"
              onChange={handlePasswordChange}
              value={password}
            />
          </div>
        </div>
        <div className="form-group row mt-3">
          <label for="confirmPassword" className="col-sm-4 col-form-label">
            Confirma a Password
          </label>
          <div className="col-sm-8">
            <input
              type="password"
              className="form-control"
              id="confirmPasswordInput"
              name="confirmPassword"
              onChange={handleConfirmPasswordChange}
              placeholder="Confirma a Password"
              value={confirmPassword}
            />
          </div>
        </div>
        <button onClick={handleClick}>Criar conta</button>
        <p>
          J√° tens uma conta? Faz o login <Link to="/login">aqui</Link>
        </p>
      </div>
    </div>
  );
}

export default Signup;
