import React from "react";

function Store(props) {
  return (
    <div className="text-center page-center">
      <h2 className="mb-3">Troféus</h2>
      {props.badges.map((badge) => {
        return (
          <div className="col-sm-6 col-md-4 col-lg-3 mx-auto">
            <div className="card m-4">
              <img
                src={badge.image}
                className="card-img-top"
                alt={"badge" + badge.id}
              />
              <div className="card-body">
                <h5 class="card-title">{badge.name}</h5>
                <p className="card-text">Multiplicador: x{badge.multiplier}</p>
                <p className="card-text">Custo: {badge.cost} pontos</p>
                <button className="btn btn-primary">Comprar</button>
              </div>
            </div>
          </div>
        );
      })}
      {props.badges.map((badge) => {
        return (
          <div className="col-sm-6 col-md-4 col-lg-3 mx-auto">
            <div className="card m-4">
              <img
                src={badge.image}
                className="card-img-top"
                alt={"badge" + badge.id}
              />
              <div className="card-body">
                <h5 class="card-title">{badge.name}</h5>
                <p className="card-text">Multiplicador: x{badge.multiplier}</p>
                <p className="card-text">Custo: {badge.cost} pontos</p>
                <button className="btn btn-primary">Comprar</button>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Store;
