import React from "react";

import "./Dice.css";

function Dice() {
  const handleClick = () => {
    rollDice();
  };

  const rollDice = () => {
    const dice = document.getElementById("dice");
    const result = Math.floor(Math.random() * 6 + 1);

    for (let i = 0; i < 6; i++) {
      dice.classList.remove("show-" + i);

      if (result === i) {
        dice.classList.add("show-" + i);
      }
    }

    setTimeout(rollDice(), 100);
  };

  return (
    <div id="dice" className="dice" onClick={handleClick}>
      <div id="dice-one-side-one" className="side">
        <div className="dot one-1"></div>
      </div>
      <div id="dice-side-two" className="side">
        <div className="dot two-1"></div>
        <div className="dot two-2"></div>
      </div>
      <div id="dice-side-three" className="side">
        <div className="dot three-1"></div>
        <div className="dot three-2"></div>
        <div className="dot three-3"></div>
      </div>
      <div id="dice-side-four" className="side">
        <div className="dot four-1"></div>
        <div className="dot four-2"></div>
        <div className="dot four-3"></div>
        <div className="dot four-4"></div>
      </div>
      <div id="dice-side-five" className="side">
        <div className="dot five-1"></div>
        <div className="dot five-2"></div>
        <div className="dot five-3"></div>
        <div className="dot five-4"></div>
        <div className="dot five-5"></div>
      </div>
      <div id="dice-side-six" className="side">
        <div className="dot six-1"></div>
        <div className="dot six-2"></div>
        <div className="dot six-3"></div>
        <div className="dot six-4"></div>
        <div className="dot six-5"></div>
        <div className="dot six-6"></div>
      </div>
    </div>
  );
}

export default Dice;
