import React from "react";

import ReactDice from "react-dice-complete";

function Dice(props) {
  return (
    <div className="text-center page-center">
      <h2>Lança o dado!</h2>
      <div className="mt-4" onClick={props.onDiceClick}>
        <ReactDice
          numDice={1}
          faceColor="#ffF"
          dotColor="#000000"
          outline={true}
          dieSize={200}
          rollTime={props.rollTime}
          rollDone={(num) => props.rollDoneCallback(num)}
          disableIndividual={true}
          ref={(dice) => (props.dice = dice)}
        />
      </div>
      <div className="mt-4">
        {props.rank !== 0 && <h4>Estás em {props.rank}º lugar</h4>}
        <h5>
          Tens {props.points} ponto{props.points !== 1 && "s"}
        </h5>
      </div>
      <button
        className="btn btn-lg btn-primary mt-4"
        onClick={props.onStoreClick}
      >
        Comprar troféus
      </button>
    </div>
  );
}

export default Dice;
