import React from "react";

function Store(props) {
  return (
    <div className="text-center page-center">
      <h2 className="m-4">Troféus</h2>
      <div className="row justify-content-center m-4">
        {props.badges.map((badge) => {
          return (
            <div className="col-sm-12 col-md-6 col-lg-3">
              <div className="card m-2">
                <img
                  src={badge.image}
                  className="card-img-top"
                  alt={"badge" + badge.id}
                />
                <div className="card-body">
                  <h5 class="card-title">{badge.name}</h5>
                  <p className="card-text mb-1 mt-3">
                    Multiplicador: x{badge.multiplier}
                  </p>
                  <p className="card-text">Custo: {badge.cost} pontos</p>
                  {!props.userBadges.find((userBadge) => userBadge.badgeId === badge.id) ? (
                      <p className="card-text mt-4">sad</p>
                  ) : (
                    <button className="btn btn-primary mt-3">Comprar</button>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>
      <button className="btn btn-lg btn-primary mt-4" onClick={props.onCloseClick}>Voltar ao jogo</button>
    </div>
  );
}

export default Store;
