import React, { Component } from "react";

import api from "../api";

import AuthContext from "../context/AuthContext";

class Admin extends Component {
  constructor(props) {
    super(props);

    this.handlePositionChange = this.handlePositionChange.bind(this);
  }

  static contextType = AuthContext;

  state = {
    players: [],
  };

  componentDidMount() {
    api
      .savedData("Histopolio")
      .then((res) => {
        this.setState({
          players: res.data,
        });
      })
      .catch((error) => {
        console.log(error.message);
      });
  }

  handlePositionChange(e) {
    const playerId = parseInt(e.target.id.substring(8));

    const newPlayers = this.state.players.map((player) => {
      if (player.userId === playerId) {
        player.position = e.target.value;
      }

      return player;
    });

    console.log(newPlayers);

    this.setState({
      players: newPlayers,
    });
  }

  handlePointsChange(e) {
    const playerId = parseInt(e.target.id.substring(8));

    const newPlayers = this.state.players.map((player) => {
      if (player.userId === playerId) {
        player.position = e.target.value;
      }

      return player;
    });

    console.log(newPlayers);

    this.setState({
      players: newPlayers,
    });
  }

  handleClick() {}

  render() {
    return (
      <div className="text-center mt-4">
        <h1>Tabela de jogadores</h1>
        <table className="table table-hover mt-4">
          <thead>
            <tr>
              <th scope="col">ID</th>
              <th scope="col">Nome</th>
              <th scope="col">Email</th>
              <th scope="col">Posição no tabuleiro</th>
              <th scope="col">Pontuação</th>
            </tr>
          </thead>
          <tbody>
            {this.state.players.map((player) => {
              return (
                <tr>
                  <th scope="row">{player.userId}</th>
                  <th>{player.name}</th>
                  <th>{player.email}</th>
                  <th>
                    <input
                      id={"position" + player.userId}
                      onChange={this.handlePositionChange}
                      type="number"
                      value={player.position}
                    />
                  </th>
                  <th>
                    <input
                      id={"points" + player.userId}
                      onChange={this.handlePositionChange}
                      type="number"
                      value={player.points}
                    />
                  </th>
                </tr>
              );
            })}
          </tbody>
        </table>
        <button
          className="btn btn-lg btn-primary mt-4"
          onClick={this.handleClick}
        >
          Guardar alterações
        </button>
      </div>
    );
  }
}

export default Admin;
