import React, { Component } from "react";

import ReactDice from "react-dice-complete";

class Dice extends Component {
  constructor(props) {
    super(props);

    this.handleDiceClick = this.handleDiceClick.bind(this);
  }

  handleDiceClick() {
    this.props.onDiceClick();
    this.reactDice.rollAll();
  }

  render() {
    return (
      <div className="text-center page-center">
        <h2>Lança o dado!</h2>
        <div className="mt-4" onClick={this.props.onDiceClick}>
          <ReactDice
            numDice={1}
            faceColor="#ffF"
            dotColor="#000000"
            outline={true}
            dieSize={200}
            rollTime={this.props.rollTime}
            rollDone={(num) => this.props.rollDoneCallback(num)}
            disableIndividual={true}
            ref={(dice) => (this.reactDice = dice)}
          />
        </div>
        <div className="mt-4">
          {this.props.rank !== 0 && <h4>Estás em {this.props.rank}º lugar</h4>}
          <h5>
            Tens {this.props.points} ponto{this.props.points !== 1 && "s"}
          </h5>
        </div>
        <button
          className="btn btn-lg btn-primary mt-4"
          onClick={this.props.onStoreClick}
        >
          Comprar troféus
        </button>
      </div>
    );
  }
}

export default Dice;
