import React, { Component } from "react";

import api from "../api";
import { useParams } from "react-router-dom";

function withParams(Component) {
  return (props) => <Component {...props} params={useParams()} />;
}

class NewDeckCard extends Component {
  constructor(props) {
    super(props);

    this.handleDeckChange = this.handleDeckChange.bind(this);
    this.handleInfoChange = this.handleInfoChange.bind(this);
    this.handlePointsChange = this.handlePointsChange.bind(this);
    this.handleActionChange = this.handleActionChange.bind(this);
    this.handleActionValueChange = this.handleActionValueChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  state = {
    deck: "communityCards",
    info: "",
    points: 0,
    action: "none",
    actionValue: "",
  };

  handleDeckChange(e) {
    this.setState({
      deck: e.target.value,
    });
  }

  handleInfoChange(e) {
    this.setState({
      info: e.target.value,
    });
  }

  handlePointsChange(e) {
    this.setState({
      points: e.target.value,
    });
  }

  handleActionChange(e) {
    this.setState({
      action: e.target.value,
    });

    if (e.target.value === "none" && e.target.value === "guess") {
      this.setState({
        actionValue: "0",
      });
    } 

  }

  handleActionValueChange(e) {
    this.setState({
      actionValue: e.target.value,
    });
  }

  handleClick() {
    // TODO: validar tudo

    const board = this.props.params.board;
    const deck = this.state.deck;
    const info = this.state.info;
    const points = parseInt(this.state.points);
    const action = this.state.action;
    const actionValue = this.state.actionValue.toString();

    const payload = { board, deck, info, points, action, actionValue };

    api
      .newDeckCard(payload)
      .then(() => {
        window.location.href = `/admin/${this.props.params.board}`;
      })
      .catch((error) => {
        console.log(error.message);
      });
  }

  render() {
    return (
      <div className="row m-4">
        <div className="col-sm-12 col-md-8 col-lg-6 mx-auto">
          <div className="form-group row">
            <label for="deck" className="col-sm-2 col-form-label">
              Baralho
            </label>
            <div className="col-sm-10">
              <select
                class="form-select"
                name="deck"
                onChange={this.handleDeckChange}
              >
                <option
                  selected={this.state.deck === "communityCards"}
                  value="communityCards"
                >
                  Decisão do Senado
                </option>
                <option
                  selected={this.state.deck === "chanceCards"}
                  value="chanceCards"
                >
                  Sorte
                </option>
              </select>
            </div>
          </div>
          <div className="form-group row mt-4">
            <label for="info" className="col-sm-2 col-form-label">
              Descrição
            </label>
            <div className="col-sm-10">
              <textarea
                className="form-control"
                id="infoInput"
                name="info"
                onChange={this.handleInfoChange}
                value={this.state.info}
                rows="3"
              />
            </div>
          </div>
          <div className="form-group row mt-4">
            <label for="points" className="col-sm-2 col-form-label">
              Pontos imediatos
            </label>
            <div className="col-sm-10">
              <input
                type="number"
                className="form-control"
                id="pointsInput"
                name="points"
                onChange={this.handlePointsChange}
                value={this.state.points}
              />
            </div>
          </div>
          <div className="form-group row mt-4">
            <label for="action" className="col-sm-2 col-form-label">
              Ação
            </label>
            <div className="col-sm-10">
              <select
                class="form-select"
                name="action"
                onChange={this.handleActionChange}
              >
                <option selected={this.state.action === "none"} value="none">
                  Sem ação
                </option>
                <option selected={this.state.action === "guess"} value="guess">
                  Adivinha
                </option>
                <option selected={this.state.action === "move"} value="move">
                  Avançar
                </option>
              </select>
            </div>
          </div>
          {this.state.action !== "none" && (
            <div className="form-group row mt-4">
              <label for="actionValue" className="col-sm-2 col-form-label">
                {this.state.action === "move" ? (
                  <div>Avançar (para recuar usar números negativos)</div>
                ) : (
                  <div>Descrição para o jogador</div>
                )}
              </label>
              <div className="col-sm-10">
                {this.state.action === "guess" ? (
                  <textarea
                    className="form-control"
                    id="actionValueInput"
                    name="actionValue"
                    onChange={this.handleActionValueChange}
                    value={this.state.actionValue}
                    rows="3"
                  />
                ) : (
                  <input
                    type="number"
                    className="form-control"
                    id="actionValueInput"
                    name="actionValue"
                    onChange={this.handleActionValueChange}
                    value={this.state.actionValue}
                  />
                )}
              </div>
            </div>
          )}
        </div>
        <div className="text-center mt-4">
          <button className="btn btn-lg btn-primary" onClick={this.handleClick}>
            Guardar carta
          </button>
        </div>
      </div>
    );
  }
}

export default withParams(NewDeckCard);
