import React, { Component } from 'react'
import { BrowserRouter, Route, Routes } from 'react-router-dom'

import Play from './../pages/Play';

class MainContent extends Component {

    constructor(props) {
        super(props);

        this.handleAnswer = this.handleAnswer.bind(this);
    }

    state = {
        gameStarted: false,
        showQuestion: false,
        question: "",
        answers: []
      };

      componentDidMount() {
        this.props.client.onopen = () => {
          console.log('WebSocket Client Connected')

          this.sendIdentificationMessage()
        }

        this.props.client.onmessage = (message) => {
          console.log(message.data)
          const dataReceived = JSON.parse(message.data)

          this.processDataReceived(dataReceived)
        }
      }

      sendIdentificationMessage() {
        const dataToSend = {
          type: 'identification',
          id: 'react'
        }

        this.sendToServer(JSON.stringify(dataToSend))
      }

      sendToServer(message) {
        this.props.client.send(message)
      }

      processDataReceived(dataReceived) {
        const command = dataReceived['type'];

        switch(command) {
          case 'question':
            this.handleQuestionReceived(dataReceived)
            break;
          default:
            console.log('Unknown message: ' + dataReceived);
        }
      }

      handleQuestionReceived(dataReceived) {
        this.setState({
          question: dataReceived['question'],
          answers: dataReceived['answers'],
          showQuestion: true
        });
      }

      handleAnswer(answerIndex) {
        this.setState({ showQuestion: false });

        const answer = answerIndex + 1;

        const dataToSend = {
          type: 'answer',
          answer: answer
        };

        this.sendToServer(JSON.stringify(dataToSend));
      }

    render() {
        return (
            <BrowserRouter>
                <Routes>
                    <Route path="play" element={<Play gameStarted={this.state.gameStarted} showQuestion={this.state.showQuestion} question={this.state.question} answers={this.state.answers} onAnswer={this.handleAnswer} /> } />
                </Routes>
            </BrowserRouter>
        );
    }
}

export default MainContent;
